export interface InsetOptions {
  /**
   * Shorthand to set the same default for all four insets: {@link insetTop},
   * {@link insetRight}, {@link insetBottom}, and {@link insetLeft}.
   */
  inset?: number;
  insetTop?: number;
  insetRight?: number;
  insetBottom?: number;
  insetLeft?: number;
}
